import * as React from "react";
  
export default function Header() {
    /*const [home_button, setHomeButton] = React.useState(false);
    const [about_button, setAboutButton] = React.useState(false);
    const [photos_button, setPhotosButton] = React.useState(false);*/

    /*const updateHomeButton = () => {
        setHomeButton( !home_button);
      };*/

    return (
        <header className="App-header">
            <h1>Portfolio</h1>
            <div className="header_buttons">
                <ButtonHome />
            
                <ButtonAbout/>
                <ButtonPhotos/>
            </div>
        </header>

    );
}

export function ButtonHome({updateHomeButton}) {
 
    const [isActive, setIsActive] = React.useState(false);

    const handleButtonClick = () => {
        setIsActive(!isActive);
    };
    
    return (
        <button onClick={event => handleButtonClick}>
            Home
        </button>
    );
}


export function ButtonAbout() {
    const [isActive, setIsActive] = React.useState(false);

    const handleButtonClick = () => {
        setIsActive(!isActive);
    };

    return (
        <button onClick={handleButtonClick}>
            About
        </button>
    );
}

export function ButtonPhotos() {
    const [isActive, setIsActive] = React.useState(false);

    const handleButtonClick = () => {
        setIsActive(!isActive);
    };

    return (
        <button onClick={handleButtonClick}>
            Photos
        </button>
    );
}
