import React, { useState } from "react";

export default function Header() {
  const [isHomeButtonActive, setIsHomeButtonActive] = useState(false);
  const [isAboutButtonActive, setIsAboutButtonActive] = useState(false);
  const [isPhotosButtonActive, setIsPhotosButtonActive] = useState(false);

  const handleHomeButtonClick = () => {
    setIsHomeButtonActive(!isHomeButtonActive);
  };

  const handleAboutButtonClick = () => {
    setIsAboutButtonActive(!isAboutButtonActive);
  };

  const handlePhotosButtonClick = () => {
    setIsPhotosButtonActive(!isPhotosButtonActive);
  };

  return (
    <header className="App-header">
      <h1>Portfolio</h1>
      <div className="header_buttons">
        <ButtonHome
          isActive={isHomeButtonActive}
          onClick={handleHomeButtonClick}
        />
        <ButtonAbout
          isActive={isAboutButtonActive}
          onClick={handleAboutButtonClick}
        />
        <ButtonPhotos
          isActive={isPhotosButtonActive}
          onClick={handlePhotosButtonClick}
        />
      </div>
    </header>
  );
}

export function ButtonHome({ isActive, onClick }) {
  return (
    <button onClick={onClick}>
      {isActive ? "Active" : "Inactive"} Home
    </button>
  );
}

export function ButtonAbout({ isActive, onClick }) {
  return (
    <button onClick={onClick}>
      {isActive ? "Active" : "Inactive"} About
    </button>
  );
}

export function ButtonPhotos({ isActive, onClick }) {
  return (
    <button onClick={onClick}>
      {isActive ? "Active" : "Inactive"} Photos
    </button>
  );
}
