import React, { useState } from "react";

export default function Header() {
  const [isHomeButtonActive, setIsHomeButtonActive] = useState(false);

  const handleHomeButtonClick = () => {
    setIsHomeButtonActive(!isHomeButtonActive);
  };

  return (
    <header className="App-header">
      <h1>Portfolio</h1>
      <div className="header_buttons">
        <ButtonHome
          isActive={isHomeButtonActive}
          onClick={handleHomeButtonClick}
        />
        <ButtonAbout />
        <ButtonPhotos />
      </div>
    </header>
  );
}

export function ButtonHome({ isActive, onClick }) {
  return (
    <button onClick={onClick}>
      {isActive ? "Active" : "Inactive"} Home
    </button>
  );
}


export function ButtonAbout() {
    const [isActive, setIsActive] = React.useState(false);

    const handleButtonClick = () => {
        setIsActive(!isActive);
    };

    return (
        <button onClick={handleButtonClick}>
            About
        </button>
    );
}

export function ButtonPhotos() {
    const [isActive, setIsActive] = React.useState(false);

    const handleButtonClick = () => {
        setIsActive(!isActive);
    };

    return (
        <button onClick={handleButtonClick}>
            Photos
        </button>
    );
}
